stepCount++;
if (stepCount === 1) {
    reportStep(stepCount, 'PASS', 'Automatic step completed');
    setTimeout(runTest, 1000);
} else if (stepCount === 2) {
    askManual('Did you see the previous step pass?').then(function(result) {
        reportStep(stepCount, result ? 'PASS' : 'FAIL', 'Manual verification ' + (result ? 'succeeded' : 'failed'));
        setTimeout(runTest, 1000);
    });
} else {
    askManual('Is everything working as expected?').then(function(result) {
        reportStep(stepCount, result ? 'PASS' : 'FAIL', 'Final verification ' + (result ? 'succeeded' : 'failed'));
        endTest(result ? 'PASS' : 'FAIL', 'Test completed with ' + (result ? 'success' : 'failures'));
    });
}
