const input = document.getElementById('test-input');
if (!input.value) {
    reportStep(1, 'PASS', 'Input field is empty');
    input.value = 'test123';
    setTimeout(runTest, 1000);
} else if (input.value === 'test123') {
    reportStep(2, 'PASS', 'Input field was populated');
    askManual('Can you see the text "test123" in the input field?').then(function(result) {
        reportStep(3, result ? 'PASS' : 'FAIL', 'Visual verification ' + (result ? 'succeeded' : 'failed'));
        endTest(result ? 'PASS' : 'FAIL', 'Test completed with ' + (result ? 'success' : 'failures'));
    });
}
