body:
checkCount++;
switch(checkCount) {
    case 1:
        reportStep(1, 'PASS', 'Starting visual checks');
        setTimeout(runTest, 1000);
        break;
    case 2:
        askManual('Is the red circle visible?').then(function(result) {
            reportStep(2, result ? 'PASS' : 'FAIL', 'Red circle visibility ' + (result ? 'confirmed' : 'failed'));
            setTimeout(runTest, 1000);
        });
        break;
    case 3:
        askManual('Is the blue square visible?').then(function(result) {
            reportStep(3, result ? 'PASS' : 'FAIL', 'Blue square visibility ' + (result ? 'confirmed' : 'failed'));
            setTimeout(runTest, 1000);
        });
        break;
    case 4:
        askManual('Are both shapes properly aligned?').then(function(result) {
            reportStep(4, result ? 'PASS' : 'FAIL', 'Shape alignment ' + (result ? 'confirmed' : 'failed'));
            endTest(result ? 'PASS' : 'FAIL', 'Visual test completed with ' + (result ? 'success' : 'failures'));
        });
        break;
}
